pragma solidity ^0.8.9;

import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "../../interfaces/validator/ICandidateManager.sol";
import "../../interfaces/validator/IRoninValidatorSet.sol";
import "../../interfaces/IRoninTrustedOrganization.sol";
import "../../interfaces/staking/IStaking.sol";
import "../../interfaces/IProfile.sol";
import "./ProfileXComponents.sol";
import { ErrUnauthorized, RoleAccess } from "../../utils/CommonErrors.sol";
import { ContractType } from "../../utils/ContractType.sol";

contract Profile is IProfile, ProfileXComponents, Initializable {
  constructor() {
    _disableInitializers();
  }

  // Pre-hook of `changeConsensusAddr` method, where this method is revert on mainnet, until it fully integrated with other components.
  modifier hookChangeConsensus() virtual {
    _;
  }

  function initialize(address validatorContract) external initializer {
    _setContract(ContractType.VALIDATOR, validatorContract);
  }

  function initializeV2(address stakingContract, address trustedOrgContract) external reinitializer(2) {
    _setContract(ContractType.STAKING, stakingContract);
    _setContract(ContractType.RONIN_TRUSTED_ORGANIZATION, trustedOrgContract);

    TConsensus[] memory validatorCandidates = IRoninValidatorSet(getContract(ContractType.VALIDATOR))
      .getValidatorCandidates();

    for (uint256 i; i < validatorCandidates.length; ++i) {
      TConsensus consensus = validatorCandidates[i];
      address id = TConsensus.unwrap(consensus);
      _consensus2Id[consensus] = id;
    }

    __migrationRenouncedCandidates();
  }

  function initializeV3(uint256 cooldown) external reinitializer(3) {
    _setCooldownConfig(cooldown);
  }

  /**
   * @dev Add addresses of renounced candidates into registry. Only called during {initializeV2}.
   */
  function __migrationRenouncedCandidates() internal virtual {}

  /**
   * @dev This method is used in REP-4 migration, which creates profile for all community-validators and renounced validators.
   * This method can be removed after REP-4 goes live.
   *
   * DO NOT use for any other purpose.
   */
  function __migrate(address id, address candidateAdmin, address treasury) internal {
    CandidateProfile storage _profile = _id2Profile[id];
    _profile.id = id;

    _setConsensus(_profile, TConsensus.wrap(id));
    _setAdmin(_profile, candidateAdmin);
    _setTreasury(_profile, payable(treasury));
    emit ProfileMigrated(id, candidateAdmin, treasury);
  }

  /**
   * @inheritdoc IProfile
   */
  function getId2Profile(address id) external view returns (CandidateProfile memory) {
    return _id2Profile[id];
  }

  /**
   * @inheritdoc IProfile
   */
  function getManyId2Consensus(address[] calldata idList) external view returns (TConsensus[] memory consensusList) {
    consensusList = new TConsensus[](idList.length);
    unchecked {
      for (uint i; i < idList.length; ++i) {
        consensusList[i] = _id2Profile[idList[i]].consensus;
      }
    }
  }

  /**
   * @inheritdoc IProfile
   */
  function getConsensus2Id(TConsensus consensus) external view returns (address) {
    return _getConsensus2Id(consensus);
  }

  /**
   * @dev Look up the `id` by `consensus`, revert if not found.
   */
  function _getConsensus2Id(TConsensus consensus) internal view returns (address) {
    (bool found, address id) = _tryGetConsensus2Id(consensus);
    if (!found) revert ErrLookUpIdFailed(consensus);
    return id;
  }

  /**
   * @inheritdoc IProfile
   */
  function tryGetConsensus2Id(TConsensus consensus) external view returns (bool found, address id) {
    return _tryGetConsensus2Id(consensus);
  }

  /**
   * @dev Try look up the `id` by `consensus`, return a boolean indicating whether the query success.
   */
  function _tryGetConsensus2Id(TConsensus consensus) internal view returns (bool found, address id) {
    id = _consensus2Id[consensus];
    found = id != address(0);
  }

  /**
   * @inheritdoc IProfile
   */
  function getManyConsensus2Id(TConsensus[] calldata consensusList) external view returns (address[] memory idList) {
    idList = new address[](consensusList.length);
    unchecked {
      for (uint i; i < consensusList.length; ++i) {
        idList[i] = _getConsensus2Id(consensusList[i]);
      }
    }
  }

  /**
   * @inheritdoc IProfile
   *
   * @dev Side-effects on other contracts:
   * - Update Staking contract:
   *    + [x] Update (id => PoolDetail) mapping in {BaseStaking.sol}.
   *    + [x] Update `_adminOfActivePoolMapping` in {BaseStaking.sol}.
   *    + [x] Move staking amount of previous admin to the the new admin.
   * - Update Validator contract:
   *    + [x] Update (id => ValidatorCandidate) mapping
   *
   * - See other side-effects for treasury in {changeTreasuryAddr}, since treasury and admin must be identical.
   */
  function changeAdminAddr(address id, address newAdminAddr) external {
    CandidateProfile storage _profile = _getId2ProfileHelper(id);
    _requireCandidateAdmin(_profile);
    _requireNonZeroAndNonDuplicated(RoleAccess.CANDIDATE_ADMIN, newAdminAddr);
    _requireCooldownPassedAndStartCooldown(_profile);
    _requireNotOnRenunciation(id);

    IStaking stakingContract = IStaking(getContract(ContractType.STAKING));
    stakingContract.execChangeAdminAddr({ poolId: id, currAdminAddr: msg.sender, newAdminAddr: newAdminAddr });

    IRoninValidatorSet validatorContract = IRoninValidatorSet(getContract(ContractType.VALIDATOR));
    validatorContract.execChangeAdminAddr(id, newAdminAddr);
    validatorContract.execChangeTreasuryAddr(id, payable(newAdminAddr));

    _setAdmin(_profile, newAdminAddr);
    _setTreasury(_profile, payable(newAdminAddr));
  }

  /**
   * @inheritdoc IProfile
   *
   * @dev Side-effects on other contracts:
   * - Update in Staking contract for Consensus address mapping:
   *   + [x] Keep the same previous pool address.
   * - Update in Validator contract for:
   *   + [x] Consensus Address mapping
   *   + [x] Bridge Address mapping
   *   + [x] Jail mapping
   *   + [x] Pending reward mapping
   *   + [x] Schedule mapping
   * - Update in Slashing contract for:
   *   + [x] Handling slash indicator
   *   + [x] Handling slash fast finality
   *   + [x] Handling slash double sign
   * - Update in Proposal contract for:
   *   + [-] Preserve the consensus address and recipient target of locked amount of emergency exit
   * - Update Trusted Org contracts:
   *   + If the current consensus is governor:
   *      - [x] Remove and delete weight of the old consensus
   *      - [x] Replace and add weight for the new consensus
   *   + If the current consensus is not governor:
   *      - [x] Do nothing
   */
  function changeConsensusAddr(address id, TConsensus newConsensusAddr) external hookChangeConsensus {
    CandidateProfile storage _profile = _getId2ProfileHelper(id);
    _requireCandidateAdmin(_profile);
    _requireNonZeroAndNonDuplicated(RoleAccess.CONSENSUS, TConsensus.unwrap(newConsensusAddr));
    _requireCooldownPassedAndStartCooldown(_profile);

    TConsensus oldConsensusAddr = _profile.consensus;

    IRoninValidatorSet validatorContract = IRoninValidatorSet(getContract(ContractType.VALIDATOR));
    validatorContract.execChangeConsensusAddr(id, newConsensusAddr);

    address trustedOrgContractAddr = getContract(ContractType.RONIN_TRUSTED_ORGANIZATION);
    (bool success, ) = trustedOrgContractAddr.call(
      abi.encodeCall(
        IRoninTrustedOrganization.execChangeConsensusAddressForTrustedOrg,
        (oldConsensusAddr, newConsensusAddr)
      )
    );

    if (!success) {
      emit ConsensusAddressOfNonGovernorChanged(id);
    }

    _setConsensus(_profile, newConsensusAddr);
  }

  /**
   * @inheritdoc IProfile
   *
   * @notice This method is not supported. Change treasury also requires changing the admin address.
   * Using the {changeAdminAddr} method instead
   *
   * @dev Side-effects on other contracts:
   * - Update Validator contract:
   *    + [x] Update (id => ValidatorCandidate) mapping
   * - Update governance admin:
   *    + [-] Update recipient in the EmergencyExitBallot to the newTreasury.
   *          Cannot impl since we cannot cancel the previous the ballot and
   *          create a new ballot on behalf of the validator contract.
   */
  function changeTreasuryAddr(address /*id */, address payable /* newTreasury */) external pure {
    revert("Not supported");
  }

  /**
   * @inheritdoc IProfile
   */
  function changePubkey(address id, bytes calldata pubkey, bytes calldata proofOfPossession) external {
    CandidateProfile storage _profile = _getId2ProfileHelper(id);
    _requireCandidateAdmin(_profile);
    _requireNonDuplicatedPubkey(pubkey);
    _verifyPubkey(pubkey, proofOfPossession);
    _requireCooldownPassedAndStartCooldown(_profile);
    _setPubkey(_profile, pubkey);
  }

  function _requireCandidateAdmin(CandidateProfile storage sProfile) internal view {
    if (
      msg.sender != sProfile.admin ||
      !IRoninValidatorSet(getContract(ContractType.VALIDATOR)).isCandidateAdminById(sProfile.id, msg.sender)
    ) revert ErrUnauthorized(msg.sig, RoleAccess.CANDIDATE_ADMIN);
  }

  function _requireNotOnRenunciation(address id) internal view {
    IRoninValidatorSet validatorContract = IRoninValidatorSet(getContract(ContractType.VALIDATOR));
    if (validatorContract.getCandidateInfoById(id).revokingTimestamp > 0) revert ErrValidatorOnRenunciation(id);
  }

  /**
   * @inheritdoc IProfile
   */
  function getCooldownConfig() external view returns (uint256) {
    return _profileChangeCooldown;
  }

  /**
   * @inheritdoc IProfile
   */
  function setCooldownConfig(uint256 cooldown) external onlyAdmin {
    _setCooldownConfig(cooldown);
  }
}
","keccak256":"0x8d7be449fd6df0d377975ad47299fbd7fa2ffc803daaef4cfdd0e5f221a3fcf0","license":"MIT"},"contracts/ronin/profile/ProfileHandler.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "../../udvts/Types.sol";
import "../../utils/RoleAccess.sol";
import { ProfileStorage } from "./ProfileStorage.sol";
import { PCUVerifyBLSPublicKey } from "../../precompile-usages/PCUVerifyBLSPublicKey.sol";

abstract contract ProfileHandler is PCUVerifyBLSPublicKey, ProfileStorage {
  /**
   * @dev Checks each element in the new profile and reverts if there is duplication with any existing profile.
   */
  function _requireNonDuplicatedInRegistry(CandidateProfile memory profile) internal view {
    _requireNonZeroAndNonDuplicated(RoleAccess.CONSENSUS, TConsensus.unwrap(profile.consensus));
    _requireNonZeroAndNonDuplicated(RoleAccess.CANDIDATE_ADMIN, profile.admin);
    _requireNonZeroAndNonDuplicated(RoleAccess.TREASURY, profile.treasury);

    // Currently skip check of governor because the address is address(0x00).
    // _requireNonDuplicated(RoleAccess.GOVERNOR, profile.__reservedGovernor);

    _requireNonDuplicatedPubkey(profile.pubkey);
  }

  function _requireNonZeroAndNonDuplicated(RoleAccess addressType, address addr) internal view {
    if (addr == address(0)) revert ErrZeroAddress(addressType);
    _requireNonDuplicated(addressType, addr);
  }

  function _requireNonDuplicated(RoleAccess addressType, address addr) internal view {
    if (_isRegisteredAddr(addr)) {
      revert ErrDuplicatedInfo(addressType, uint256(uint160(addr)));
    }
  }

  function _isRegisteredAddr(address addr) internal view returns (bool) {
    return _registry[uint256(uint160(addr))];
  }

  function _requireNonDuplicatedPubkey(bytes memory pubkey) internal view {
    if (_isRegisteredPubkey(pubkey)) {
      revert ErrDuplicatedPubkey(pubkey);
    }
  }

  function _isRegisteredPubkey(bytes memory pubkey) internal view returns (bool) {
    return _registry[_hashPubkey(pubkey)];
  }

  function _verifyPubkey(bytes calldata publicKey, bytes calldata proofOfPossession) internal {
    if (!_pcVerifyBLSPublicKey(publicKey, proofOfPossession)) {
      revert ErrInvalidProofOfPossession(publicKey, proofOfPossession);
    } else {
      emit PubkeyVerified(publicKey, proofOfPossession);
    }
  }

  function _requireCooldownPassed(CandidateProfile storage _profile) internal view {
    if (block.timestamp < _profile.profileLastChange + _profileChangeCooldown) {
      revert ErrProfileChangeCooldownNotEnded();
    }
  }

  function _requireCooldownPassedAndStartCooldown(CandidateProfile storage _profile) internal {
    _requireCooldownPassed(_profile);
    _startCooldown(_profile);
  }
}
","keccak256":"0x51366f17ffc305ab982cdb7639562489926f6b0d4927fa08b44427ef5c733e12","license":"MIT"},"contracts/ronin/profile/ProfileStorage.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "../../udvts/Types.sol";
import "../../extensions/collections/HasContracts.sol";
import "../../utils/RoleAccess.sol";
import { IProfile } from "../../interfaces/IProfile.sol";

abstract contract ProfileStorage is IProfile, HasContracts {
  /// @dev Mapping from id address => candidate profile.
  mapping(address => CandidateProfile) internal _id2Profile;

  /**
   * @dev Mapping from any address or keccak256(pubkey) => whether it is already registered.
   * This registry can only be toggled to `true` and NOT vice versa. All registered values
   * cannot be reused.
   */
  mapping(uint256 => bool) internal _registry;

  /// @dev Mapping from consensus address => id address.
  mapping(TConsensus => address) internal _consensus2Id;

  /// @dev The cooldown time to change any info in the profile.
  uint256 internal _profileChangeCooldown;

  /// @dev Upgradeable gap.
  bytes32[47] __gap;

  /**
   * @dev Add a profile from memory to storage.
   */
  function _addNewProfile(CandidateProfile storage _profile, CandidateProfile memory newProfile) internal {
    _profile.id = newProfile.id;

    _setConsensus(_profile, newProfile.consensus);
    _setAdmin(_profile, newProfile.admin);
    _setTreasury(_profile, newProfile.treasury);
    _setGovernor(_profile, newProfile.__reservedGovernor);
    _setPubkey(_profile, newProfile.pubkey);

    emit ProfileAdded(newProfile.id);
  }

  function _setConsensus(CandidateProfile storage _profile, TConsensus consensus) internal {
    // Backup old consensus
    _profile.oldConsensus = _profile.consensus;

    // Delete old consensus in mapping
    delete _consensus2Id[_profile.consensus];
    _consensus2Id[consensus] = _profile.id;

    // Set new consensus
    _profile.consensus = consensus;
    _registry[uint256(uint160(TConsensus.unwrap(consensus)))] = true;

    emit ProfileAddressChanged(_profile.id, RoleAccess.CONSENSUS, TConsensus.unwrap(consensus));
  }

  function _setAdmin(CandidateProfile storage _profile, address admin) internal {
    _profile.admin = admin;
    _registry[uint256(uint160(admin))] = true;

    emit ProfileAddressChanged(_profile.id, RoleAccess.CANDIDATE_ADMIN, admin);
  }

  function _setTreasury(CandidateProfile storage _profile, address payable treasury) internal {
    _profile.treasury = treasury;
    _registry[uint256(uint160(address(treasury)))] = true;

    emit ProfileAddressChanged(_profile.id, RoleAccess.TREASURY, treasury);
  }

  /**
   * @dev Allow to registry a profile without governor address since not all validators are governing validators.
   */
  function _setGovernor(CandidateProfile storage _profile, address governor) internal {
    _profile.__reservedGovernor = governor;
    if (governor != address(0)) {
      _registry[uint256(uint160(governor))] = true;
    }
  }

  function _setPubkey(CandidateProfile storage _profile, bytes memory pubkey) internal {
    if (_profile.pubkey.length != 0) {
      _profile.oldPubkey = _profile.pubkey;
    }

    _profile.pubkey = pubkey;
    _registry[_hashPubkey(pubkey)] = true;

    emit PubkeyChanged(_profile.id, pubkey);
  }

  function _startCooldown(CandidateProfile storage _profile) internal {
    _profile.profileLastChange = block.timestamp;
  }

  /**
   * @dev Get an existed profile struct from `id`. Revert if the profile does not exists.
   */
  function _getId2ProfileHelper(address id) internal view returns (CandidateProfile storage _profile) {
    _profile = _id2Profile[id];
    if (_profile.id == address(0)) revert ErrNonExistentProfile();
  }

  /**
   * @dev Returns hash of a public key.
   */
  function _hashPubkey(bytes memory pubkey) internal pure returns (uint256) {
    return uint256(keccak256(pubkey));
  }

  function _setCooldownConfig(uint256 cooldown) internal {
    _profileChangeCooldown = cooldown;
  }
}
","keccak256":"0x60bef3f865bcc25582f5f598d1a145613d2f78efdfcd4113066b576ce3d7fa7c","license":"MIT"},"contracts/ronin/profile/ProfileXComponents.sol":{"content":"// SPDX-License-Identifier: MIT

import "../../interfaces/IProfile.sol";
import { ContractType } from "../../utils/ContractType.sol";
import "./ProfileHandler.sol";

pragma solidity ^0.8.9;

abstract contract ProfileXComponents is IProfile, ProfileHandler {
  /**
   * @inheritdoc IProfile
   */
  function execApplyValidatorCandidate(
    address admin,
    address id,
    address treasury,
    bytes calldata pubkey,
    bytes calldata proofOfPossession
  ) external override onlyContract(ContractType.STAKING) {
    // Check existent profile
    CandidateProfile storage _profile = _id2Profile[id];
    if (_profile.id != address(0)) revert ErrExistentProfile();

    // Validate the info and add the profile
    CandidateProfile memory profile = CandidateProfile({
      id: id,
      consensus: TConsensus.wrap(id),
      admin: admin,
      treasury: payable(treasury),
      __reservedGovernor: address(0),
      pubkey: pubkey,
      profileLastChange: 0,
      oldPubkey: "",
      oldConsensus: TConsensus.wrap(address(0))
    });
    _requireNonDuplicatedInRegistry(profile);
    _verifyPubkey(pubkey, proofOfPossession);
    _addNewProfile(_profile, profile);
  }

  /**
   * @inheritdoc IProfile
   */
  function arePublicKeysRegistered(bytes[][2] calldata listOfPublicKey) external view returns (bool) {
    for (uint i; i < listOfPublicKey.length; ) {
      for (uint j; j < listOfPublicKey[i].length; ) {
        if (!_isRegisteredPubkey(listOfPublicKey[i][j])) {
          return false;
        }

        unchecked {
          j++;
        }
      }

      unchecked {
        i++;
      }
    }

    return true;
  }
}
","keccak256":"0xa8069f6ab02902de6c6ac4dedcf21991af63f810a9f3ec242ac5dfff056c53bf","license":"MIT"},"contracts/ronin/profile/Profile_Testnet.sol":{"content":"// SPDX-License-Identifier: MIT

import "./Profile.sol";

pragma solidity ^0.8.9;

contract Profile_Testnet is Profile {
  function migrateOldConsensusList(address[] calldata lId, TConsensus[] calldata lCss) external onlyAdmin {
    require(lId.length == lCss.length, "Invalid length");

    CandidateProfile storage _profile;
    for (uint i; i < lCss.length; ++i) {
      _profile = _id2Profile[lId[i]];
      _profile.oldConsensus = lCss[i];
    }
  }

  function migrateRenouncedCandidate() external onlyAdmin {
    __defaultMigrate();
  }

  function manualMigrate(address id, address candidateAdmin, address treasury) external onlyAdmin {
    __migrate(id, candidateAdmin, treasury);
  }

  function __migrationRenouncedCandidates() internal override onlyInitializing {
    __defaultMigrate();
  }

  function __defaultMigrate() private {
    if (block.chainid != 2021) return;

    address[76] memory lConsensus = __consensuses();
    address[76] memory lAdmin = __admins();
    address[76] memory lTreasury = __treasuries();

    for (uint i; i < lConsensus.length; ++i) {
      __migrate(lConsensus[i], lAdmin[i], lTreasury[i]);
    }
  }

  function __admins() private pure returns (address[76] memory list) {
